import React, { useState } from 'react';

export default function ServiceBooking({ token }){
  const [vehicleType, setVehicleType] = useState('car');
  const [desc, setDesc] = useState('');
  const [address, setAddress] = useState('');

  const submit = async e => {
    e.preventDefault();
    if (!token) return alert('Login required');
    const res = await fetch('http://localhost:5000/api/services', {
      method:'POST',
      headers:{
        'Content-Type':'application/json',
        Authorization: 'Bearer '+token
      },
      body: JSON.stringify({ vehicleType, description: desc, address })
    });
    const data = await res.json();
    if (data._id) alert('Service team will reach you within an hour');
    else alert(data.msg || 'Failed');
  };

  return (
    <div>
      <h2>On-site Service Booking</h2>
      <form onSubmit={submit}>
        <div>
          <label>Vehicle:</label>
          <select value={vehicleType} onChange={e=>setVehicleType(e.target.value)}>
            <option value="car">Car</option>
            <option value="bike">Bike</option>
          </select>
        </div>
        <div><textarea placeholder="Describe issue" value={desc} onChange={e=>setDesc(e.target.value)} /></div>
        <div><input placeholder="Address" value={address} onChange={e=>setAddress(e.target.value)} /></div>
        <button type="submit">Book Service</button>
      </form>
    </div>
  );
}
