import React from "react";

const HeroSection = () => {
  return (
    <section
      className="relative bg-cover bg-center h-[90vh] flex items-center text-white"
      style={{
        backgroundImage:
          "url('https://images.unsplash.com/photo-1549989476-69a92fa57c36?auto=format&fit=crop&w=1920&q=80')",
      }}
    >
      <div className="bg-black bg-opacity-50 absolute inset-0"></div>

      <div className="relative z-10 px-10 md:px-20 max-w-3xl">
        <h4 className="text-lg font-semibold mb-3 text-green-400">
          Revolutionising Fuel Delivery with Every Click
        </h4>
        <h1 className="text-4xl md:text-6xl font-bold mb-6 leading-tight">
          Redefining the Power Behind Smart & Simple Energy Distribution
        </h1>

        <div className="flex items-center space-x-6">
          <button className="bg-green-500 text-black font-semibold px-6 py-3 rounded-md hover:bg-green-400">
            Fuel Up Now
          </button>
          <button className="flex items-center space-x-2 border border-white px-6 py-3 rounded-md hover:bg-white hover:text-black transition">
            <span className="text-2xl">â–¶</span>
            <span>Our Video</span>
          </button>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
