import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

export default function BookingPage({ token }) {
  const [fuels, setFuels] = useState([]); // To store fuels from API
  const [selectedFuelId, setSelectedFuelId] = useState('');
  const [liters, setLiters] = useState('');
  const [address, setAddress] = useState('');
  
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const navigate = useNavigate();

  // 1. Fetch available fuels when the component loads
  useEffect(() => {
    // Redirect to login if there's no token
    if (!token) {
      navigate('/login');
      return;
    }

    const fetchFuels = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/fuels');
        if (response.ok) {
          const data = await response.json();
          setFuels(data);
          if (data.length > 0) {
            setSelectedFuelId(data[0]._id); // Default to the first fuel
          }
        } else {
          setError('Could not fetch fuel types.');
        }
      } catch (err) {
        setError('Network error. Could not fetch fuels.');
      }
    };

    fetchFuels();
  }, [token, navigate]); // Dependency array

  // 2. Handle the form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess(false);

    if (!token) {
      setError('You must be logged in to make a booking.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/bookings', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          // IMPORTANT: Send the auth token
          'Authorization': `Bearer ${token}` 
        },
        body: JSON.stringify({
          fuelId: selectedFuelId,
          liters: Number(liters),
          address: address
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setSuccess(true);
        setLiters('');
        setAddress('');
        // You could show a success message or redirect them to their dashboard
        alert('Booking successful! Your fuel is on the way.');
        navigate('/dashboard');
      } else {
        setError(data.msg || 'Booking failed. Please check stock or details.');
      }
    } catch (err) {
      setError('Network error. Please try again later.');
    }
  };

  return (
    <div className="p-4 md:p-8">
      <div className="max-w-lg mx-auto bg-white rounded-lg shadow-lg p-8">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">
          Book Your Fuel
        </h2>
        <p className="text-center text-gray-600 mb-8">
          Fast, reliable, and delivered to you.
        </p>

        <form onSubmit={handleSubmit}>
          {error && <p className="text-red-500 text-center mb-4">{error}</p>}
          {success && (
            <p className="text-green-500 text-center mb-4">
              Booking Confirmed!
            </p>
          )}

          <div className="mb-4">
            <label htmlFor="fuelType" className="block text-gray-700 font-medium mb-2">
              Select Fuel Type
            </label>
            <select
              id="fuelType"
              className="w-full p-3 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={selectedFuelId}
              onChange={(e) => setSelectedFuelId(e.target.value)}
              required
            >
              <option value="" disabled>Loading fuels...</option>
              {fuels.map((fuel) => (
                <option key={fuel._id} value={fuel._id}>
                  {fuel.name} - â‚¹{fuel.pricePerLiter}/liter
                </option>
              ))}
            </select>
          </div>

          <div className="mb-4">
            <label htmlFor="liters" className="block text-gray-700 font-medium mb-2">
              Quantity (Liters)
            </label>
            <input
              type="number"
              id="liters"
              className="w-full p-3 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="e.g., 20"
              min="1"
              value={liters}
              onChange={(e) => setLiters(e.target.value)}
              required
            />
          </div>

          <div className="mb-6">
            <label htmlFor="address" className="block text-gray-700 font-medium mb-2">
              Delivery Address
            </label>
            <textarea
              id="address"
              rows="4"
              className="w-full p-3 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Enter your full delivery address"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              required
            ></textarea>
          </div>

          <button
            type="submit"
            className="w-full bg-green-600 hover:bg-green-700 text-white font-bold py-3 rounded-lg transition duration-300"
            disabled={!token || fuels.length === 0}
          >
            Confirm Booking
          </button>
        </form>
      </div>
    </div>
  );
}
