import React, { useState } from "react";
import axios from "axios";

function BookingPage() {
  const [fuelType, setFuelType] = useState("");
  const [quantity, setQuantity] = useState("");
  const [location, setLocation] = useState({ lat: null, lng: null });
  const [eta, setEta] = useState(null);

  // ‚úÖ Get user's current location
  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          const { latitude, longitude } = pos.coords;
          setLocation({ lat: latitude, lng: longitude });
          alert("üìç Location captured successfully!");
        },
        (err) => {
          alert("‚ùå Location access denied!");
        }
      );
    } else {
      alert("‚ùå Geolocation is not supported by your browser.");
    }
  };

  // ‚úÖ Submit booking
  const handleBooking = async () => {
    if (!fuelType || !quantity || !location.lat) {
      alert("Please fill all fields and capture your location!");
      return;
    }

    const res = await axios.post("http://localhost:5000/api/bookings", {
      fuelType,
      quantity,
      location,
    });

    setEta(res.data.eta);
  };

  return (
    <div className="container mt-5">
      <h2>üõª Book Your Fuel</h2>
      <div>
        <label>Fuel Type:</label>
        <select
          value={fuelType}
          onChange={(e) => setFuelType(e.target.value)}
        >
          <option value="">Select</option>
          <option value="Petrol">Petrol</option>
          <option value="Diesel">Diesel</option>
        </select>
      </div>

      <div>
        <label>Quantity (Liters):</label>
        <input
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
        />
      </div>

      <button onClick={getLocation}>üìç Capture Location</button>
      <button onClick={handleBooking}>Confirm Booking</button>

      {eta && <p>‚è∞ Estimated delivery time: {eta}</p>}
    </div>
  );
}

export default BookingPage;
